        if (!empty($imageFileSmall)) {
            //Mage::getDesign()->getSkinBaseDir() . DS . 'images' . DS . 'widget';
            
            // File Upload 
            try {
                $file = array();
                $file['tmp_name'] = $_FILES['groups']['tmp_name']['design']['fields']['small_inline_image']['value'];
